import { genkit, z } from "genkit";
import { gemini15Flash, googleAI } from "@genkit-ai/googleai";
import genkitEndpoint from "@/lib/genkit-endpoint";

const ai = genkit({
  plugins: [googleAI()],
  model: gemini15Flash,
});

const getWeather = ai.defineTool(
  {
    name: "getWeather",
    description: "Gets the current weather in a given location",
    inputSchema: z.object({
      location: z.string().describe("The location to get the current weather for"),
    }),
    outputSchema: z.object({
      temperature: z.number().describe("The current temperature in degrees Fahrenheit"),
      condition: z.enum(["sunny", "cloudy", "rainy", "snowy"]).describe("The current weather condition"),
    }),
  },
  async ({ location }) => {
    // Fake weather data
    const randomTemp = Math.floor(Math.random() * 30) + 50; // Random temp between 50 and 80
    const conditions = ["sunny", "cloudy", "rainy", "snowy"];
    const randomCondition = conditions[Math.floor(Math.random() * conditions.length)];

    return { temperature: randomTemp, condition: randomCondition };
  }
);


const rollDice = ai.defineTool(
  {
    name: "rollDice",
    description: "Rolls a six-sided die",
    inputSchema: z.object({}),
    outputSchema: z.number().int().min(1).max(6),
  },
  async () => {
    return Math.floor(Math.random() * 6) + 1;
  }
);

export const POST = genkitEndpoint(
  {
    schema: z.object({
      system: z.string(),
      messages: z.array(
        z.object({
          role: z.enum(["user", "assistant"]),
          content: z.string(),
        })
      ),
      prompt: z.string(),
    }),
  },
  async ({ system, messages, prompt }) => {
    const chat = ai.chat({ system, tools: [getWeather, rollDice] });

    // send all of the history and the new message to the chat
    const response = await chat.send({messages, prompt});

    return response;

  }
);

"use client";

import { Card, CardContent } from "@/components/ui/card";
import { Textarea } from "@/components/ui/textarea";
import DemoConfig from "@/lib/demo-config";
import { Label } from "@radix-ui/react-label";
import { useContext } from "react";

export default function ToolCallingChatbotConfig() {
  const { config, setConfig } = useContext(DemoConfig);

  return (
    <Card>
      <CardContent>
        <Label htmlFor="form-system" className="mt-3 mb-2 block">
          Custom System Message
        </Label>
        <Textarea
          placeholder="You are a helpful assistant. Try to answer the user's queries to the best of your ability."
          value={config?.system}
          id="form-system"
          name="system"
          onInput={(e) => {
            setConfig?.({ system: (e.target as HTMLTextAreaElement).value });
          }}
        />
      </CardContent>
    </Card>
  );
}

"use client";
import Chat from "@/components/chat";
import { LucideIcon, Sun, Cloud, CloudRain, CloudSnow } from "lucide-react";

function WeatherResponse({
  temperature,
  condition,
}: {
  temperature: number;
  condition: "sunny" | "cloudy" | "rainy" | "snowy";
}) {
  let icon: LucideIcon = Sun;
  switch (condition) {
    case "cloudy":
      icon = Cloud;
      break;
    case "rainy":
      icon = CloudRain;
      break;
    case "snowy":
      icon = CloudSnow;
      break;
  }

  return (
    <div className="flex items-center gap-2">
      <span className="text-xl">{temperature}Â°F</span>
      <span className="text-xl">{icon}</span>
    </div>
  );
}

function DiceResponse({ output }: { output: number }) {
  const pips = Array.from({ length: output }, (_, i) => (
    <span key={i} className="h-2 w-2 rounded-full bg-white"></span>
  ));

  return (
    <div className="relative h-12 w-12 flex items-center justify-center rounded-md bg-red-500">
      <div className="absolute flex flex-wrap content-around h-8 w-8 items-center justify-center gap-1">
          {pips}
      </div>
    </div>
  );
}


export default function ToolCallingChatbotApp() {
  return (
    <Chat
      endpoint="/tool-calling-chat/api"
      renderPart={(part) => {
        if (part.toolResponse?.name === "getWeather")
          return <WeatherResponse {...part.toolResponse.output} />;
        if (part.toolResponse?.name === "rollDice")
          return <DiceResponse output={part.toolResponse.output} />;
        return null;
      }}
    />
  );
}

import Demo from "@/components/demo";
import ToolCallingChatbotConfig from "./config";
import ToolCallingChatbotApp from "./app";

export default async function Page() {
  return (
    <Demo
      name="tool-calling-chat"
      title="Tool Calling Chatbot"
      Config={ToolCallingChatbotConfig}
    >
      <ToolCallingChatbotApp/>
    </Demo>
  );
}
